var Opmo = require('./Opmo');

class GlobalFile extends Opmo {
  constructor(data){
    super(data);
  }
  generate(){
    var content = "";
    content += this.genUtility();
    super.exportFile(content);
  }
  genUtility(){
    var util =
`
(defun stretch (dur pitch)
  (let '(ret nil) '(ret-len nil)
    (setf ret (gen-tuplet 1 1 'm 'n dur (length (flatten pitch))))
    (return-from stretch ret)
  )
)
`;
    return util;
  }
}

module.exports = GlobalFile;
